export default class Ping {

	text: string;

	constructor(text : string) {
		this.text = text;
	}

	toString() : string {
		return 'ping ' + this.text;
	}

	duplicate() : Ping {
		this.text = this.text + " " + this.text;
		return this;
	}

	static create(text: string) : Ping {
		return new Ping(text);
	}
};
