import * as http from "http";
import * as url from "url";
import * as express from "express";
import * as bodyParser from "body-parser";
import errorHandler = require("errorhandler");
import methodOverride = require("method-override");
import PingApplicationService from 'application/PingApplicationService';

var app = express();

app.use(bodyParser.urlencoded({ extended: true }));
app.use(bodyParser.json());
app.use(methodOverride());

var env = process.env.NODE_ENV || 'development';
if (env === 'development') {
    app.use(errorHandler());
}

app.get('/', (req, res) => {
    res.send('Hello World!');
});

app.get('/ping/', (req, res) => {
    res.send("RESULT: " + PingApplicationService.create().getPing("nobody").toString());
});

app.get('/ping/:name', (req, res) => {
    res.send("RESULT: " + PingApplicationService.create().getPing(req.params.name).toString());
});

app.listen(3333, function(){
    console.log("Demo Express server listening on port %d in %s mode", 3333, env);
});

